mapper.select:
SELECT t.id, t.title, t.saleable, t.valid, t.`name` as brandName, t2.create_time, t2.last_update_time, t2.sub_title, t2.brand_id, t2.categoryName , t2.cid1, t2.cid2, t2.cid3 FROM ( SELECT s.id, s.title, s.saleable, s.valid, b.NAME FROM tb_spu s, tb_brand b WHERE s.brand_id = b.id ) t INNER JOIN ( SELECT s.*, GROUP_CONCAT( c.NAME SEPARATOR '/' ) AS categoryName FROM tb_spu s, tb_category c WHERE s.cid1 = c.id OR s.cid2 = c.id OR s.cid3 = c.id GROUP BY s.id ) t2 ON t.id = t2.id
and title like concat('%',#{search},'%')
and t2.saleable = #{saleable}
ORDER BY ${sortBy} ${sort}

mapper.insert:
insert into tb_spu (title, sub_title, cid1,
  cid2, cid3, brand_id, saleable,
  `valid`, create_time, last_update_time
  )
values (#{title,jdbcType=VARCHAR}, #{subTitle,jdbcType=VARCHAR}, #{cid1,jdbcType=BIGINT},
  #{cid2,jdbcType=BIGINT}, #{cid3,jdbcType=BIGINT}, #{brandId,jdbcType=BIGINT}, #{saleable,jdbcType=BOOLEAN},
  #{valid,jdbcType=BOOLEAN}, #{createTime,jdbcType=TIMESTAMP}, #{lastUpdateTime,jdbcType=TIMESTAMP}
  )
